(3, 4, '高起本', '网页设计', '北京大学网页设计班', '', '北大网设班，是北大新开设的一个班。。。。', 5678, NULL, '', '', '全日制', '12个月', '2012年3月', '本科5年', '90分', 0, '2010-09-21', 3, 0),
