INSERT INTO `lxkkinfo` (`lxk_id`, `lxk_gjid`, `lxk_sid`, `lxk_title`, `lxk_zy`, `lxk_pic`, `lxk_con`, `lxk_xuefei`, `lxk_click`, `lxk_tjbool`, `lxk_date`) VALUES
(1, 1, 1, '邓迪大学留学保送班', '行政管理', 'upload/1312443516.jpg', '<p>英国邓大，是与双威签订保送协议的唯一一所国外大学。</p>', '26000元', 0, 1, '2011-08-04'),
(2, 2, 2, '旧金山MBA', 'MBA总裁培训班', 'upload/1312444270.jpg', '<p>旧金山大学 ( 简称 USF) 成立于 1855 年 , 地处旧金山市，校舍位置得天独厚，矗立在全市七个山峰之一的山顶上，远眺整个风景优美的旧金山湾和金门桥，俯瞰旧金山华埠，而市中心就在脚底。该学校自建校以来，以培养学生刻苦修习学业，服务社会的精神为目的。学校设立的课程和学术研究项目给教师、学生及研究人员各种机会，使他们通过在校的学习、研究工作提高自身的学术素养及专业水平，成为 21 世纪所需要的成功人才。</p>', '310000', 0, 1, '2011-08-04'),
(4, 1, 3, '英国剑桥工商管理总裁培训班', '工商管理', 'upload/1312446974.jpg', '<p>英国剑桥工商管理总裁培训班是剑桥大学对外国留学生最早开设的一个班。</p>', '120000元', 0, 1, '2011-08-04'),
(5, 2, 2, '旧金山大学经管留学保送班', '经济管理', 'upload/1312522082.jpg', '<p>旧金山大学开设的工商管理专业</p>', '245550元', 0, 1, '2011-08-05'),
(6, 1, 3, '商务英语精英班', '商务英语', 'upload/1312527947.jpg', '<p>名师教学，保学保会，就业有保证。</p>', '56000元', 0, 1, '2011-08-05');
INSERT INTO `lxnews` (`lx_nid`, `lx_gjcl`, `lx_ncl`, `lx_ntitle`, `lx_npic`, `lx_nsp`, `lx_ncon`, `lx_nbool`, `lx_nauthor`, `lx_nclick`, `lx_jzdate`, `lx_ndate`) VALUES
(28, '2', 'cgal', '上海交通大学双咸报名点成功牵引一名留学生', 'upload/1310628477.jpg', 'http://', '<p>上海交通大学双咸报名点成功牵引一名留学生</p>\r\n<p>&nbsp;</p>\r\n<p>此学生要去留学美国，双威报名点，为其联系了美国最好的哈福大学，并为其办理的签证手续。待三年的国外学习生活过后，此学生将成为具有国外学士学位的海归一族。</p>', 1, '刘一冰', 39, NULL, '2011-07-14 15:26:13'),
(29, '3', 'hggl', '加拿大留学生王者归来', '', 'http://', '<p>留学生王非同日前从加拿大学满归国</p>\r\n<p>王非同同学在加国升造以前专历为大专，通过交通大学网院，双威直属报名点进行报名留学，一切顺利通过。今喜得加国硕士学位证书。</p>', 1, '马小飞', 30, NULL, '2011-07-15 10:53:16'),
(30, '5', 'cgal', '赴韩留学李大龙7月20日学业期满', '', 'http://', '<p>李大龙喜获韩国工商管理硕士学位证书。</p>', 1, '双威江老师', 27, NULL, '2011-07-15 11:35:16'),
(31, '6', 'mjhw', '海外出游新概念', '', 'http://www.google.com/gd/2563.html', '<p>荷兰三日游</p>', 1, '蒋三国', 26, NULL, '2011-07-15 12:05:38'),
(32, '1', '0', '2012英国硕士申请时间规划', '', 'http://', '<p><span class="STYLE2">2012英国硕士申请时间规划：</span><br />\r\n<span class="STYLE30"><strong>大学三年级：</strong>2011年5-6月 咨询留学规划老师，了解留学费用、办理流程，<br />\r\n与家长商议确认留学事宜（我想了解英国留学费用）<br />\r\n2011年7-9月 准备语言、咨询专业老师、选校定专业、准备材料<br />\r\n<strong>大学四年级：</strong>2011.9-10月 办理在读证明和成绩单等官方手续<br />\r\n2011.9-11月 递交申请材料<br />\r\n2011.11-2012.3月 等待offer，选择院校并确认 <br />\r\n2012.4-5月：设置个性化的签证计划、办理护照和准备各种签证材料<br />\r\n2012.6-7月：处理和翻译全部的签证材料并办理签证<br />\r\n2012.7-8月：订购机票，安排住宿、准备行李</span></p>', 1, '', 47, NULL, '2011-07-20 16:19:29'),
(33, '1', '0', '2012英国硕士申请规划', '', 'http://', '<p>　　近几年，留英学生人数不断增长，英国名校申请，尤其是学制短、名校多、含金量高的英国硕士申请竞争更加激烈。2011年英国留学签证政策可谓波起云 涌，如今英国签证新政已尘埃落定，各位不以英国签证多变而改变留英目的矢志不渝的留英生可以埋头学习，昂首准备开始2012年的留英规划啦。由于英国院校 申请是采取&quot;rolling&quot;，即滚动录取，先到先得。如何能够在2012年的英国名校申请中脱颖而出，抢得先机占领有限名额， 威久留学专家为您指点迷津。提前做好留学规划，确定好留学国家和专业后，通过威久留学专家的帮助，2012年，放飞理想，留学去！</p>', 1, '', 32, NULL, '2011-07-20 16:40:55'),
(34, '1', '0', '艺术生英国留学指导', '', 'http://', '<p>最近关注CCTV6电影频道推出的大型创意体验活动《创意星空》第二季的朋友应该注意到一个出色的选手&mdash;&mdash;卢霆希，Nottingham Trent   University的服装设计系高材生，短小精干、波澜不惊的外表下有一颗情感丰富的心，并能将内心的情感丰富在设计上。作为从事留学方面事业的我其实 更在意的是他的学习背景：Nottingham Trent   University的服装设计系高材生，毕业作品被选送参加伦敦毕业生服装周；于2007年被保送就读伦敦服装学院服装设计与服装手工艺硕士，在 2009年2月以一级荣誉毕业生的身份毕业，毕业作品被邀请参加伦敦多个艺术展览；在读书期间已经开始在多个著名的工作室实习；2010年回到香港创办了 自己的工作室。细心地朋友也许会发现，其实创意星空第一季的冠军&mdash;&mdash;赵钎焱，参加比赛时是英国圣马丁艺术与设计学院毕业班在读生。我相信英国艺术留学生的 经历是他们能够在《创意星空》舞台上如此给力的基础和保证。</p>', 1, '', 28, NULL, '2011-07-20 16:44:41'),
(35, '1', '0', '英国的艺术设计课程概况', '', 'http://', '艺术和设计类学生通常被要求将他们以往的作品提供给招生者。由于不能类比的原因，每所院校的每个系都有不同的入学要求，没有统一的全国标准。艺术与设计教育在英国教育中占了相当的地位，英国的艺术设计课程有三个层次：职业课程、本科课程和研究生课程。<br />\r\n1. 艺术和设计职业课程。学制一般为一年或两年，学成后授予的文凭种类有：全国通用职业资格文凭、职业性中学高级水平考试文凭、国家高级学位证书或其他文凭。毕业后从事戏剧表演、剧院后台工作，进入时装节或从事平面设计工作。<br />\r\n2. 本科课程。学制一般是三年或四年，包括所选专修科目的理论和实践的教学。英国有大量此类学位课程可供选择，这些包括戏剧、电影和电视专业、摄像和数码传媒专业和20世纪音乐专业的文学学士课程。这些课程的学科导师一般是专业领域的从业人员，许多院校将专业资格作为学位课程学习的组成部分。<br />\r\n3. 研究生课程。分讲授式研究生课程和研究式研究生课程两种。<br />\r\n艺术和设计类学生通常被要求将他们以往的作品提供给招生者。由于不能类比的原因，每所院校的每个系都有不同的入学要求，没有统一的全国标准。</p>\r\n<p>&nbsp;</p>', 1, '', 50, NULL, '2011-07-20 16:50:44'),
(36, '1', '0', '申请英国大学的艺术类专业，需要满足哪些条件', '', 'http://', '<p>1.要有艺术作品。这是申请英国大学艺术类专业最重要的部分，也就是说你申请某个艺术专业，要有这个专业相应的艺术作品。申请时最好把你的艺术作品编成集寄过去，这样对申请会更有帮助。<br />\r\n2.大学成绩单和学士学位。申请人要提供大学本科成绩单(GPA)和学士学位。<br />\r\n3.个人陈述（P.S.）和推荐信。个人陈述和申请其它专业的都差不多，推荐信要由教过你的教授或者专业人士来写，一般三封或四封。<br />\r\n4.语言成绩。一般雅思成绩6.5分左右，如果学生成绩在5.5分左右可以匹配相应的语言课程，所以学生要在考虑去英国留学前，最好能先学习报考雅思。</p>', 1, '', 16, NULL, '2011-07-20 16:54:51'),
(37, '1', '0', '选择去英国读艺术专业的理由', '', 'http://', '<p>最近关注CCTV6电影频道推出的大型创意体验活动《创意星空》第二季的朋友应该注意到一个出色的选手&mdash;&mdash;卢霆希，Nottingham Trent   University的服装设计系高材生，短小精干、波澜不惊的外表下有一颗情感丰富的心，并能将内心的情感丰富在设计上。作为从事留学方面事业的我其实 更在意的是他的学习背景：Nottingham Trent   University的服装设计系高材生，毕业作品被选送参加伦敦毕业生服装周；于2007年被保送就读伦敦服装学院服装设计与服装手工艺硕士，在 2009年2月以一级荣誉毕业生的身份毕业，毕业作品被邀请参加伦敦多个艺术展览；在读书期间已经开始在多个著名的工作室实习；2010年回到香港创办了 自己的工作室。细心地朋友也许会发现，其实创意星空第一季的冠军&mdash;&mdash;赵钎焱，参加比赛时是英国圣马丁艺术与设计学院毕业班在读生。我相信英国艺术留学生的 经历是他们能够在《创意星空》舞台上如此给力的基础和保证。</p>', 1, '', 20, NULL, '2011-07-20 16:55:21'),
