INSERT INTO `pxkkinfo` (`pxk_id`, `pxk_cl`, `pxk_sid`, `pxk_title`, `pxk_pic`, `pxk_xfei`, `pxk_yhui`, `pxk_time`, `pxk_xshi`, `pxk_adder`, `pxk_tel`, `pxk_qq`, `pxk_line`, `pxk_duix`, `pxk_key`, `pxk_click`, `pxk_con`, `pxk_date`, `pxk_bool`) VALUES
(5, 17, 4, '上海市林业局二级考试培训班', NULL, '5555', '4444', '2011年3月16日', '1年半', '上海张杨路256号3楼301', '021-65234187', '313081169', '111路，222路，地铁6号线', '林业相关人员', NULL, 41, '<h1>2<a class="tit" id="tit" target="_blank" mon="a=7" href="http://news.dayoo.com/china/201102/28/53868_15358250.htm">全国各省区两会选举结束 18位省级一把手履新</a></h1>\r\n<div class="abstract">2011-02-28 09:16&nbsp;&nbsp;&nbsp;来源：大洋网</div>\r\n<div id="main_richtext"><!--富摘要-->\r\n<p class="first">据新华社报道，湖北省第十一届人大第四次会议2月26日补选湖北省委书记李鸿忠为湖北省人大常委会主任，补选省委副书记王国生为省长。此前两天，该省另一位省委副书记杨松当选为该省政协主席，海南省委副书记于迅当选为海南省政协主席。至此，全国31个省市自治区今年&ldquo;两会&rdquo;人事变动尘埃落定，共选举产生4位省市人大常委会主任、4位省长和10位省市区政协主席。</p>\r\n<p>据相关资料，记者发现，18位新任高官均为&ldquo;50后&rdquo;。而4省市卸任的人大常委会主任均出生于1945年，已达正省级高官65岁退休年龄。</p>\r\n<p>曾先后担任过安徽和浙江省委副书记职务的浙江省政协主席乔传秀，成为今年各地&ldquo;两会&rdquo;中履新的唯一正省级女性高官。不过由于湖北省政协主席宋育英的卸任，我国现任省级政协主席中女性仍为4位。</p>\r\n<p><strong>新当选的省级政协主席目前均非全国政协委员。</strong></p>\r\n<p>类似的情形以前也曾有过。2009年1月，岳福洪、薛延忠分别当选辽宁、山西省政协主席；2010年初，王正福、刘伟、黄龙云分别当选贵州、山东、广东省政协主席，上述5人均在2010年2月以特别邀请人士身份被增补为全国政协委员并参加了3月份的全国政协会议。</p>\r\n<p>在卸任的10位省级政协主席中，6人已经达到65岁正省级高官退休年龄，2人将在今年内达到退休年龄。</p>\r\n</div>', '2011-02-27', 1),
