INSERT INTO `qp_zhongxin` (`qp_id`, `qp_con`, `npic`) VALUES
(1, '<p>一休网，整合中国最科学最完备的咨询、管理和师资，旨在打造中国最好的教育服务平台，为国际国内企业、政府机构、中学等组织团体提供优质的专业化培训服务。<br />\r\n以人性化理念为基础，以卓越的观念为导航，以贴心的服务为准则，与您共享精良的企业课程，为企业团体的自我发展、自我管理提供个性化培训方案。以满足企业团体的自身需求，提高企业团体的市场竞争力，促进以自我发展为基础的教育训练，让所有的企业在有效培训中不断创新，更快发展。</p>', 'upload/1311057850.jpg');
INSERT INTO `school` (`s_id`, `s_name`, `s_banner`, `s_logo`, `s_bxys`, `s_xyjs`, `s_zsdx`, `s_xxhj`, `s_kkxx`, `s_bmxz`, `s_xlxw`, `s_click`, `s_bj`) VALUES
(1, '西南科技大学网院', 'upload/1313997516.jpg', '', '学校建有研究生院，建立了完整的学士―硕士―博士培养体系，共拥有55个博士、132个硕士学位授权点,70个本科专业。设有20个院和3个系，拥有各类学生6万余人。学校师资力量雄厚，现有教师2000余名，教授、副教授1000余名，中国科学院院士3人，中国工程院院士2人。另外还聘请了30余位中国科学院院士、中国工程院院士为我校兼职教授。\r\n', '<p>西南科技大学是一所理、工、文、管、经、法、农、教育学相结合的全日制多科性普通高等学校。学校十分重视继续教育教育的发展，依托学校的师资和教学资源，始终坚持办学规模、结构、质量、效益和特色的统一，规范管理，以提高教学质量为生命，办学声誉逐年提高。现有各类成教、网络和自考学生近5万人。<br />\r\n西南科技大学网络教育学院上海学习中心依托建设单位上海应用技术学院继续教育学院，学院坐落于上海市徐汇区桂林路，应用技术学院继续教育学院是上海应用技术学院设立的成人高等教育办学实体，多年来依托学校丰富的办学资源和条件，现已形成了以本、专科成人学历教育为主，各类中短期非学历教育培训为辅，自学考试和现代远程教育两翼齐飞的多层次、多形式、多渠道的办学体系。现在校生规模近15000人，其中成人学历教育在校生规模达5000人。<br />\r\n西南科技大学网络教育学院上海学习中心是西南科技大学在上海市唯一拥有合法身份的学习中心，中心将秉承西南科技大学和应用技术学院的办学精神，规范招生和教学，努力做好支持服务工作。</p>', '1、具有国民教育系列大专及以上文凭的人员可报读专科起点本科。 \r\n2、具有高中（含普通高中、职高、技校、中专）及以上学历人员可报读高中起点专科。 \r\n', '<p>环境犹如仙境一般。</p>', '<p>\r\n<table cellspacing="0" cellpadding="4" border="0" style="width: 245px; height: 153px" class="border2">\r\n    <tbody>\r\n        <tr>\r\n            <td width="70" valign="top" height="87" bgcolor="#e1e7ee" align="right">开设专业：</td>\r\n            <td valign="top" bgcolor="#ffffff"><font size="-0">工商管理、会计学、行政管理、土木工程（道路与桥梁、工民建方向）、计算机科学与技术（数字娱乐与动漫游戏方向</font></td>\r\n        </tr>\r\n        <tr>\r\n            <td height="21" bgcolor="#ccd4e1" align="right">开学时间：</td>\r\n            <td bgcolor="#ffffff">&nbsp;2010年9月</td>\r\n        </tr>\r\n        <tr>\r\n            <td height="21" bgcolor="#ccd4e1" align="right">上课地点：</td>\r\n            <td bgcolor="#ffffff">&nbsp;上海学习中心</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</p>', '<p>西南科技大学网院</p>\r\n<p>请学员携带以下物品至一休教育网报名：<br />\r\n身份证复印件1张（A4纸张）<br />\r\n最高学历复印件1张（A4纸张）<br />\r\n免冠1寸近照8张<br />\r\n报名费120元<br />\r\n考试费120元<br />\r\n电子档照片、学历证书及身份证<br />\r\n提醒：所提供的相关证件必须真实有效。<br />\r\n&nbsp;</p>', '<p>毕业颁发学位证书。</p>', 595, 1),
(2, '上海交通大学网院', 'upload/1289871129.jpg', '', '办学较早，师资历量雄厚', '<p>上海交通大学创办于1896年，是中国历史最悠久、综合实力最强的著名大学之一。上海交通大学继续教育学院成立于2000年7月，学院自成立起始终贯彻 &ldquo;练好内功，稳步发展，保证质量，形成特色&rdquo;的办学方针，率先提出&ldquo;质量是网络教育的生命线&rdquo;的口号。2005年以来，学院的教学科研工作取得重要突破， &ldquo;多校合作、面向西部，创建基于天地网的新型教育资源共享体系&rdquo;项目获得国家级教学成果奖一等奖（排名第一），&ldquo;天地网远程教育关键技术、系列产品及应 用&rdquo;获得国家科技进步奖二等奖（排名第二），&ldquo;现代远程教育支撑平台研究及示范应用&rdquo;获得上海市科技进步奖一等奖（排名第一）。          学院立足上海、辐射江浙、面向西部，构建了比较完善的技术支撑平台和现代远程教育体系；现有校外学习中心20个，在册学生15,000余名、毕业生 13,000余名。</p>', '面向社会，层次不限', '<p>交大环境优美。</p>', '<p>开设，理工班，设计班，语言班，舞蹈班</p>', '<p>一定要带上身分证，学历证明，还有报名费。</p>', '<p>本科三年，硕士四年。</p>', 245, 0),
(4, '北京交通大学', 'upload/1285569355.jpg', 'upload/1291973154.jpg', '百年来，数代交大人励精图治、艰苦奋斗，学校现已发展成为一所以工、管为主体，以信息与通信类和管理与经济类学科群为特色，工、管、经、理、文相结合的多科性重点大学，并成为培养铁路现代化建设高水平、高层次人才和解决铁路现代化建设与改革重大技术与政策问题的重要基地。 \r\n　　近年来，学校各方面的发展又取得了长足的进步，校园面貌日新月异、教学质量和科研水平稳步提高、学术交流日益活跃、办学实力不断增强。拥有通信与信息系统、信号与信息处理、交通信息工程与控制、交通运输规划与管理、产业经济学等5个国家级重点学科，11个部级重点学科；硕士点50个，博士点21个，并建有信息与通信工程、交通运输工程、产业经济学、土木工程、机械工程、控制科学与工程学科的6个博士后科研流动站。 \r\n　　北方交通大学师资力量雄厚，现有教职工2500余人，其中从事教学与科研的人员1100余人、中国科学院和工程院院士5人、教授164人、副教授403人。迄今造就了一大批知名学者、工程技术专家和高级管理干部。全日制在校学生近15000人，其中硕、博研究生2300余人，外国留学生100多人，函授、夜大、脱产班学生5000余人。', '<p><span style="font-size: xx-large;">　<big>北京交通大学是中国近现代历史上创办最早的大学之一，也是中国第一所专门培养管理型人才的高等学府。<br />\r\n学校具有百年历史，是教育部直属、由教育部和铁道部共建的全国重点大学，是全国首批博士、硕士学位授予高校，首批进入国家&ldquo;211工程&rdquo;建设的高校和&ldquo;985工程&rdquo;项目重点建设高校之一， 1997年和2006年先后被评为&ldquo;全国本科教学工作优秀学校&rdquo;。目前学校已成为培养国家经济社会发展、信息、交通，特别是轨道交通现代化建设高水平、高层次人才，解决国家经济和社会发展以及铁路现代化建设与改革重大技术与政策问题的重要基地。也是教育部批准开展现代远程教育的试点高校之一。<br />\r\n&ldquo;饮水思源，爱国荣校&rdquo;，如今，有着112年辉煌历史的北京交通大学，肩负着新的历史使命，秉承&ldquo;知行&rdquo;校训，以谦虚谨慎、开拓进取的精神，努力实现交通大学百年华诞时江泽民同志&ldquo;继往开来，勇攀高峰，把交通大</big></span><big>学建设成世界一流大学&rdquo;的要求和期望，向着国内一流、国际知名的研究型大学的目标迈进。</big><br />\r\n&nbsp;</p>', '1.报考高中起点专科的考生须具有高中、中专、中技、职高或同等学力毕业证书。 \r\n2.报考专科起点本科的考生须具有教育部审定核准的国民教育系列高等学校或高等教育自学考试机构颁发的大学专科（及大专以上）毕业证书。 \r\n3.报读专升本层次的学生，报读时年龄需满21周岁；报读高中起点层次的，报读时年龄需满18周岁。 \r\n4.在秋季报读的学生，其证书取得时间不得晚于当年8月31日。 \r\n5.考生应具备上网学习的条件，且能够保证参加网络学习的时间。 \r\n6.各专业均面向社会招生。', '<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>大专毕业证书</p>\r\n<p><img src="http://www.edulife.com.cn/schoolphoto/zsSchool_bjjt_2006418142136_SchoolPicPath.jpg" alt="" /></p>\r\n<p>本科毕业证书</p>\r\n<p><img src="http://www.edulife.com.cn/schoolphoto/zsSchool_bjjt_2006418142154_SchoolPicPath.jpg" alt="" /></p>', '<table width="245" cellspacing="0" cellpadding="4" border="0" class="border2">\r\n    <tbody>\r\n        <tr>\r\n            <td width="70" valign="top" height="87" bgcolor="#e1e7ee" align="right">开设专业：</td>\r\n            <td valign="top" bgcolor="#ffffff"><font size="+0">物流管理、汽车运用技术、城市轨道交通、计算机应用技术、工商企业管理、会计、物流管理、计算机科学与技术、工商管理、会计学</font></td>\r\n        </tr>\r\n        <tr>\r\n            <td height="21" bgcolor="#ccd4e1" align="right">开学时间：</td>\r\n            <td bgcolor="#ffffff">&nbsp;2010年3月</td>\r\n        </tr>\r\n        <tr>\r\n            <td height="21" bgcolor="#ccd4e1" align="right">上课地点：</td>\r\n            <td bgcolor="#ffffff">&nbsp;上海学习中心</td>\r\n        </tr>\r\n    </tbody>\r\n</table>', '<p><span id="lblBmxz">免报名费、入学测试</span></p>', '<p><span id="lblByjy"><a href="http://www.edulife.com.cn/sh/UpLoad/pic/200907/271127323107.jpg" target="_blank"><img width="230" height="160" border="0" src="http://www.edulife.com.cn/sh/UpLoad/pic/200907/271127323107.jpg" alt="" /></a> <br />\r\n<a href="http://www.edulife.com.cn/sh/UpLoad/pic/200907/271128466017.jpg" target="_blank"><img width="230" height="160" border="0" src="http://www.edulife.com.cn/sh/UpLoad/pic/200907/271128466017.jpg" alt="" /></a> <br />\r\n<a href="http://www.edulife.com.cn/sh/UpLoad/pic/200907/27112945488.jpg" target="_blank"><img width="230" height="160" border="0" src="http://www.edulife.com.cn/sh/UpLoad/pic/200907/27112945488.jpg" alt="" /></a></span></p>', 205, 0),
(5, '重庆师范网院', 'upload/1285549409.gif', NULL, '办学早，师资优。', '', '普通学生', '<p>好。</p>', '<p>十月到十一月开设免费课程。</p>', '<p>一。带上身分证。</p>\r\n<p>二。带上通知书。</p>', '<p>专科二年。</p>', 123, 0),
(6, '北京涉外艺术学院', 'upload/1285485233.jpg', 'upload/1291973541.jpg', '   本院毕业的各专业学生，基本是社会需求量大，艺术要求较高的专业人才。学生只要专业学得好，综合素质较高，加上名师推荐，比较容易找到理想的工作。其就业方向主要是军队和地方文艺团体从事艺术表演（演员）、学校和企业事业单位，政府机关的文化部门从事各类教育工作（老师或辅导员）、广播电视台（播音员、主持人）、国家公务员（干部）等。也可继续攻读研究生，取得硕士学位。本院今年首届（04级）学生，有2名分别被解放军总政歌舞团、海军军乐团征聘。另有3名已分别留学美国长岛音乐学院和俄罗斯莫斯科大学继续学习艺术。其他学生也分别进入社会找到自己理想的职业。在鹇学生有被中央歌剧院聘请参加排练和演出的，有被邀请参加国际性大型赛事的，也有被邀请到中央电视台作节目的。', '<p><font size="+0">北京涉外艺术学院是一所以音乐为主体、多专业融合的全日制高等艺术学院，是中央音乐学院的直属教学点。学院全方位依托中央音乐学院和中央戏剧学院、北京舞蹈学院的教学资源，所有专业老师均有以上学院教授担任，精心组织教学活动，旨在培养专业基础较好，有志于艺术天地驰聘的考生实现成才的梦想。所有课程考试及格，颁发中央音乐学院毕业证书，符合条件的还可申请学士学位证书。同时，本院学生有出国意愿的，可直接申请赴美国长岛音乐学院和韩国多的高校留学，学院负责办理相关手续。<br />\r\n</font><font size="+0">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 学院座落在世界著名历史文化遗产&mdash;&mdash;北京十三陵风景区，风景秀丽、空气清新，学院占地300多亩，拥有大型塑胶运动场、琴房、电脑机房、形体房、语音室、图书管和演艺礼堂，各种教学和生活设置齐全，是一个理想的学习场所。<br />\r\n</font></p>', '   1．具有高中、中专毕业或同等学历，爱好相关艺术并有一定专业基础的。 \r\n       2．凡通过本院专业考试合格（有参加其他院校加试成绩的可以参考或参加各省专业联考符合我院专业录取标准的发放专业合格证书；文化考试后符合我院录取分数的发放录取通知书。注：音乐表演专业必须参加我院组织的专业考试，不合格者可以转音乐教育或本院其他专业。', '<p>&nbsp;</p>\r\n<p>北京涉外艺术学院校区</p>\r\n<p><img alt="" src="http://www.edulife.com.cn/sh/UpLoad/pic/200903/111714441846.jpg" /></p>', '<p>\r\n<table class="border2" border="0" cellspacing="0" cellpadding="4" width="245">\r\n    <tbody>\r\n        <tr>\r\n            <td bgcolor="#e1e7ee" height="87" valign="top" width="70" align="right">开设专业：</td>\r\n            <td bgcolor="#ffffff" valign="top">\r\n            <p><font face="Arial">音乐教育、通俗、美声、民族、钢琴、大号、小号、圆号、长号、次中音号、小低音号、单簧管、双簧管、巴松、长笛、萨克斯、小提琴、中提琴、大提琴、低音提琴、坚琴、打击乐、二胡、琵琶、扬琴、竹笛、古筝、笙、阮等、军乐方向、音乐剧、中国古典舞、民族民间舞、现代舞、舞蹈编导、影视表演、播音与主持、艺术管理、音乐表演考前培训</font></p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor="#ccd4e1" height="21" align="right">开学时间：</td>\r\n            <td bgcolor="#ffffff">2010年9月上旬</td>\r\n        </tr>\r\n        <tr>\r\n            <td bgcolor="#ccd4e1" height="21" align="right">上课地点：</td>\r\n            <td bgcolor="#ffffff">北京校区</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</p>', '<p><span id="lblBmxz">请学员携带以下物品至教育人生网报名： <br />\r\n身份证复印件1张 <br />\r\n最高学历复印件1张 <br />\r\n免冠1寸近照2张 <br />\r\n报名费100元 <br />\r\n<font color="#ff0000">提醒：所提供的相关证件必须真实有效。</font></span></p>', '<div class="border3"><span id="lblByjy"><font face="Arial">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 学生完成教学计划规定的全部课程，修满规定的学分，可获得中央音乐学院颁发的毕业证书，国家承认学历。 <br />\r\n<font face="Arial">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 满足学位授予规定，即全部课程及格，其中补考不超过2门（含2门），论文成绩良好以上，可由中央音乐学院学位评定委员会授予学士学位。</font></font></span></div>', 404, 0),
(9, '上海财务管理进修学院', 'upload/1290668788.jpg', 'upload/1291000781.gif', '1.教育评估合格单位 专注开设财大专业——学院是上海市首批44家自考评估合格单位之一， “中英采购与供应管理职业资格认证”上海地区2009年度优秀助学单位。学院自成立以来以专业开办于财经大学自考专业为品牌特色。\r\n\r\n2.名师执教、针对性强、确保高合格率——来自上海财经大学、上海现代物流中心及采购行业的专家、教授能针对考试大纲、准确把握考试的重点难点，考前集中串讲，确保考试高合格率。\r\n\r\n3.地理位置优越，交通便利——学院毗邻上海财经大学、上海外国语大学可享受高校资源，大学食堂就餐。周边交通便利，紧邻轻轨三号线赤峰路站和多个公交站点。\r\n\r\n 4.学院承诺一次付费，持续服务——在考试中出现单科课程不合格者，可在下期开班时免费重听。\r\n\r\n', '<p>&nbsp;上海财务管理学院成立于1996年，是沪上最专业的会计培训机构之一，年平均培训会计专业人才近万人次。学院一直以优秀的师资，严格的教学管理，完善的教育体系，深受广大学员的好评。<br />\r\n学院主要项目：自考助学、高级研修课程、会计类职业资格培训、网校远程教育、网站开发与维护。<br />\r\n学院因业务发展需要，诚邀以下职位人才加盟，欢迎每一位致力于事业腾飞的人士加盟，与我们一起大展宏图、共创理想。<br />\r\n&nbsp;</p>', '  \r\n   　 \r\n会计专业（国家重点学科） \r\n \r\n    会计专业是上海财经大学的金牌专业，是中国仅有的2个国家级重点会计学科之一。 会计学院拥有的会计学博士生导师居全国第一，会计学研究方向的齐全在全国同学科中名列第一，在综合科研水平上居全国第一，上海财经大学会计专业毕业生一直备受各大企业的青睐，每年的毕业生供不应求。\r\n　 \r\n采购与供应专业（中英合作）\r\n \r\n    随着世界经济一体化步伐的加快，中国正成为全球采购的重要地区，需要大量系统掌握采购与供应管理理论的人才，但目前国内高校很少开设此专业，采购与供应人才不足的矛盾较为突出，为此全国高等教育自学考试委员会联合CIPS英国皇家采购与供应学会特别推出本专业，一考三证，为社会培养高层次采购供应管理人员。\r\n \r\n', '<p>\r\n<table width="750" cellspacing="0" cellpadding="0" border="0" id="table152">\r\n    <tbody>\r\n        <tr>\r\n            <td>\r\n            <p style="line-height: 150%;">&nbsp; 学院配有数个多媒体教室，电脑机房，全天开放自习教室，班主任全程跟踪，让学生充分利用业余时间，保证顺利通过考试。全日制自考生在校期间，同时参加我院各类职业资格类课程的培训，使学生在获得学历文凭的同时也能取得相关的职业资格证书，为毕业后的就业提供有力的保障。</p>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td>\r\n            <table width="100%" cellspacing="0" cellpadding="0" border="0" id="table153">\r\n                <tbody>\r\n                    <tr>\r\n                        <td align="center" style="text-align: center;"><img width="184" height="148" border="0" src="http://www.accounting.sh.cn/web/zk/img/zk-js.gif" alt="" /></td>\r\n                        <td align="center" style="text-align: center;"><img width="184" height="148" border="0" src="http://www.accounting.sh.cn/web/zk/img/zk-jf.gif" alt="" /></td>\r\n                    </tr>\r\n                </tbody>\r\n            </table>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td>\r\n            <table width="100%" cellspacing="0" cellpadding="0" border="0" id="table156">\r\n                <tbody>\r\n                    <tr>\r\n                        <td align="center" colspan="2" style="text-align: center;">\r\n                        <p style="text-align: left; line-height: 150%;"><font color="#808080">　　</font>学院为外省市学生及需要住校学习的学生提供住宿。管理严格，设施齐全，每间都配有空调、衣柜、书桌等。 财大标准间、财大六人间、学院四人间、学院六人间、 学院八人间。</p>\r\n                        </td>\r\n                    </tr>\r\n                    <tr>\r\n                        <td align="center" style="text-align: center;"><img width="184" height="148" border="0" src="http://www.accounting.sh.cn/web/zk/img/zs2.gif" alt="" /></td>\r\n                        <td align="center" style="text-align: center;"><img width="184" height="148" border="0" src="http://www.accounting.sh.cn/web/zk/img/zs1.gif" alt="" /></td>\r\n                    </tr>\r\n                </tbody>\r\n            </table>\r\n            </td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</p>', '<p>&nbsp;</p>\r\n<div><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">★课程名称：（中英合作）采购与供应管理专业（独立本科段）</span></div>\r\n<div><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">★开课日期：</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">2010</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">年</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">5</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">月</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">周日</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">学</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">制：一年半</span></div>\r\n<div><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">★报名电话：</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">021-55290808&nbsp;&nbsp;&nbsp; 65368713 &nbsp;65366664</span></div>\r\n<div><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">★上课地址：上海市虹口区西体育会路</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">369</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">号</span>&nbsp;</div>\r\n<div><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">★交通路线：地铁三号线（赤峰路站），</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">537</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">、</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">829</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">、</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">139</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">、</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">21</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">等多条公交线路，公交便捷。</span></div>\r\n<div><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">★招生对象：凡属国民教育系列的各类专科及以上毕业生（含在校生）</span></div>\r\n<div><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">★使用教材：《全国自学考试辅导教材》、《</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">CIPS</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">认证教材》</span></div>\r\n<div><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">★师资力量：上海财经大学教授、上海现代物流中心推荐优秀讲师及采购行业高管</span></div>\r\n<div><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">★毕业证书：上海财经大学自考本科文凭；中国采购与供应管理职业资格证书（</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">CPS</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">）、英国皇家采购与供应国际证书（</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">CIPS</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">）</span></div>\r\n<div style="text-indent: -57.75pt; margin-left: 57.75pt;"><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">★费</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">&nbsp;&nbsp; </span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">用：</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">全部学费：</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">13500</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">元</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">（不含考试费、教材费等）。一次性付清可享</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">9.5</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">折</span></div>\r\n<div style="text-indent: -57.75pt; margin-left: 57.75pt;"><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">一次性收取教材资料费</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">800</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">元</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">&nbsp;&nbsp;&nbsp;&nbsp; </span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">报名费：</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">50</span><span style="color: rgb(51, 51, 51); font-size: 10.5pt;">元</span></div>', '<p>课程介绍 1．凡会计上岗证全科班面授课程和会计电算化课程均于本院就读，优惠100元。 2．凡参加我院指定会计系列培训的学员，报名参加会计实务操作班，可优惠学费100元。 课程特色 开班后我院统一收取照片，集体办理考试报名。一般在培训结束后的一个月以内即可参加考试。 适合学员 凡有志于从事财务会计类工作的人员均可报名培训。 授课教师 长期从事会计资深教师执教。 课程设置 计算机基础知识、中文Windows 2000、Excel 2000电子表格、Word 2000文字处理、网络基础操作、会计电算化知识、通用会计核算教学软件</p>', '<p>样图暂无。</p>', 905, 0),
(11, '上海021艺术服务中心', 'upload/1291882664.gif', '', '学校特色\r\n    　　东方童画提倡“娱乐教学法”、“激发教学法”、“鼓励教学法”，是国内外先进教学法，提出“在绘画中了解孩子、肯定孩子” “教画画不在于急着传授技能技巧，而在于唤醒和鼓舞，激发学生的创作热情”，改变过去传统的“模仿”教学模式，着重培养孩子的兴趣、观察力与创作力。课程设置方面，东方童画按专业分有儿童画、泥塑、手工、国画、版画、油画、素描、动漫等不同专业;按年龄分类从2岁半的“幼启小小班”到13岁“学生美术预备班”，各个年龄层次班级，难度不断加强。 \r\n东方童画汇聚了国内优秀儿童美术教师、专家50多名，以系统的教学理念及方式进行教学。基地教师为全职教师，除上课外每天积极准备教案及课前准备、课后总结，每月聚集一起商议讨论教学计划、互相观摩并共同讨论课题。 ', '<p>&nbsp;&nbsp; &nbsp;东方童画是知名的上海卢湾区凌尔艺文化艺术进修学校旗下少儿美术培训机构，拥有学生数量上千人。上海021艺术已成立8年，拥有四大品牌：白领美术俱乐部、高考美术直通车、音乐教育会所、东方童画，有着先进的教学理念、一流师资队伍、统一的教育管理模式，在上海艺术教育中取得了傲人成绩。 <br />\r\n目前拥有四大型教育基地：徐家汇教学基地、浦东教学基地、虹口教学基地、卢湾教学基地。其中徐家汇教学基地是总部，面积最大、开办时间最长，面积1000平，是国内最大的专业艺术教育场所之一，东方童画以国际的视野、创新的精神、专业的教师队伍、独特的教学方式受到广大家长及专家的好评，被誉为&ldquo;上海艺术教育培训最佳成长奖&rdquo;。 <br />\r\n&nbsp;&nbsp;&nbsp;&nbsp; 目前，东方童画已与俄罗斯、法国、意大利等国家、地区的艺术团体建立了良好的交流关系，通过不断交流来提升学校视野与教学、创新。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp; 在国内，我们与各大艺术院校、优秀艺术教育团体建立了交流关系，并参观学习;同时我们与上海书城、上海正大艺术馆、儿童图书馆等场所建立了良好的交流关系，并适时举办活动宝宝 啊，丰富学生视野，努力争做少儿美术教育前沿。</p>', '幼儿，成人。', '<p>上海影城点[长宁区] <br />\r\n分校地址 长宁区新华路160号（靠近番愚路）上海影城2楼 <br />\r\n分校交通 138路（番愚路站） 26路、506路（淮海西路番愚路） 44路（华山路淮海西路） 945路（华山路番愚路） 76路（新华路番愚路） 946路、48路（香花桥）地铁三、四号线（虹桥路站） <br />\r\n&nbsp;</p>\r\n<p>浦东分部教学区[浦东区] <br />\r\n分校地址 张杨路601号华铖大厦8017号 <br />\r\n分校交通 地铁2号线 82路 86路 130路 隧道三线 隧道五线 隧道六线 778路 大桥五线 <br />\r\n&nbsp;</p>', '<div class="top04"><span id="top01_labAppraise">上海021艺术服务中心提供儿童绘画培训、幼儿启蒙培训、少儿钢琴培训、少儿舞蹈培训等青少儿培训辅导课程，还有成人美术、陶艺、绘画、雕刻等培训课程。</span></div>', '<p>如要报名，请先在网上看好具体要求。然后要填写招名要求。</p>', '<p>无</p>', 250, 0),
(12, '上海外国语大学', 'upload/1291947699.jpg', '', '1、引进优质教育资源，提高以英语为媒介学习国外大学课程的综合能力 \r\nPGA国际课程为使学生适应英语国家的大学课程学习而采用特定的教学方法，在三年学习中提供给学生720小时（课堂）+360小时（自主学习）的学习时间，大大提高了学生的英语综合运用和表达能力。学生完全可以在国内接受到西方课堂教学环境（包括教学方法、学习方法等），使学生毕业后在国外能够较好地适应西方教育环境。 \r\n2、与国际教育接轨 \r\n通过高中三年的系统学习，PGA国际课程班的学生不仅掌握国内高中教育所要求的必备知识，而且具备直接入读国外大学的资格及学术能力。通过学习，学生不仅掌握运用学术英语进行交流和研究的能力、独立学习、思考的能力，而且熟悉了国外的教学方法、考评方法以及文化差异，提高个人的综合实力。 \r\n3、减轻升学压力，开辟国际升学出路 \r\nPGA国际课程增加了中国高中教育供给的多样化和选择性，减轻了高考的升学压力。学生在完成PGA国际课程所有课程学习后，成绩合格，可以直接升入美国、英国、加拿大、澳大利亚等英语国家的100多所国际大学就读本科学位，同时还有数千所名牌海外大学供学生选择申请。 \r\n4、严谨的教学体系和优质师资力量 \r\nPGA国际课程是引进多国认可的国际课程体系，并结合中国高中教学的内容，通过国内专家论证，研制开发的一套适合中国高中学生的课程。美国ACT Education Solutions, Limited (AES)对本课程实施全程教学质量监控及相关支持。课程由拥有专业教师资格并具有丰富教学经验的外籍教师担任，秉承西方教学理念，注重学生能力培养。 \r\n5、成熟的国际教育管理经验 \r\n我们的合作单位中教国际教育交流中心长期致力于中外合作办学项目的研发，有成熟的运作和管理经验。PGA高中国际课程已陆续在北京、上海、成都、新疆、苏州、郑州、西安等地的重点中学顺利开班，并取得了良好社会效应。目前，各中心教学效果初显，学生学习积极性高涨。仅仅经过一个学期的语言强化，学生的英语运用能力，尤其是听说能力突飞猛进，写作能力明显优于国内普通班级，英语综合成绩普遍高于国内教育同级水平。 \r\n6、ACT考试成绩——开启美国大学的金钥匙 \r\n美国3000多所大学把ACT成绩（俗称：美国高考）作为入学标准，哈佛、耶鲁、宾夕法尼亚、哥伦比亚等众多美国名校，依据ACT成绩决定录取和奖学金授予，美国大学教育委员会（ACE）推荐旗下的1800所大学减免学分（美国大学一个学分=400～600美元，减免学分就意味着学生可以节省学习费用和学习时间）。 \r\n7、近百所中国教育部认可的美国大学任您选 \r\n中教国际教育交流中心凭借其二十多年来积累的国际教育交流资源为依托，与全美100余所大学建立了直接合作关系，包括全美综合排名120以内的知名大学，如：宾夕法尼亚大学、乔治•盛顿大学、俄亥俄州立大学、丹佛大学、纽约州立石溪大学、俄罗冈大学等，以及部分专业方面各具特色的大学加入，可满足学生不同层次、个性化的需求。 \r\n8、全封闭教学，全面学习生活管理 \r\n上海外国语大学实验学校PGA国际班，每班配有专职班主任负责学生管理，本课程班采用外教为主小班授课（25人小班制），全寄宿制封闭教学，按照我校的统一标准严格实施学生管理和生活管理。', '<p>上海外国语大学实验学校是一所12年一贯制的寄宿制学校，我校与上海外国语大学附属外国语学校和附属外国语小学有着紧密的教育教学联系。学校正副校长由上外附中的领导担任，并与上外附中形成师资共享，通过教研、教学、讲座、培训等互动形式打造良好的师资队伍。本校与上外附中采用统一校训、统一标饰，统一教材，凸显外语教学优势。外语课实行小班化、全外语教学。我校还开设第二外语：德语、法语。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 学生毕业时，小学优秀毕业生将推荐给上外附中，再由上外附中择优录取。我校享有向上海外国语大学推荐优秀高三毕业生的资格，并由上海外国语大学提前择优录取。我校美术特长班学生在报考复旦、交大、上大等艺术类专业有较大优势。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 学校占地180亩，建筑面积6万平方米，融汇了大量中外知名学校建筑风格。教学设施齐全，拥有理、化、生实验室、语音室、视听室、电子阅览室、计算机房、音乐室、美术室、劳技室等各类专用教室以及室内体育馆，400米塑胶标准运动场、篮球场、网球场等，还拥有条件优越的学生食堂和4人标准间宿舍。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 我校与澳大利亚、加拿大、法国等国家的学校结成了姐妹学校，互派教师、学生进行教学交流，并与澳大利亚、加拿大的学校合作办学，开展1+2课程（高中第一年在国内就读，高中第二、第三年在国外就读）。我校已与中教国际教育交流中心合作开办PGA(高中国际课程班)，本课程班的最大特点是中外双方在课程上进行部分融合，学生完成所有课程学习要求后，可直接在我校参加ACT（美国高考）考试，并可直接升入将近120所以英语为母语国家的美国、加拿大、英国、澳大利亚等海外大学就读本科学位课程。<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 我校热烈欢迎各位有志的学子报考。我校将竭诚为你们服务！<br />\r\n<br />\r\n&nbsp;</p>', '小学：符合年龄条件（六周岁）的身体健康，行为规范良好的儿童； \r\n初中：身体健康，学习成绩良好，品行端正的小学五年级毕业生； \r\n高中：身体健康，品学兼优，中考达到我校录取分数线的初三毕业生； \r\n中澳合作班：身体健康，品学兼优，应届初中毕业生。（招生人数：40名）', '<p>环境优美，设备其全。</p>', '', '<div style="text-align: left; padding-left: 12px; padding-right: 12px;"><b>报名提示：</b><span id="lblBmxz">　1、参加本市统一中考的应届初中毕业生，填报志愿&ldquo;上海外国语大学实验学校&rdquo;，全市统一录取时上海外国语大学实验学校择优录取。 <br />\r\n2、其他初中应届毕业生，可持当地中考成绩及相关材料，直接到上海市广元西路43号301室报名，经面试和考核合格，即可录取。 <br />\r\n入学考试主要测试学生的英语综合能力和数学基础。 <br />\r\n文化课成绩：录取分数不低于中招考试文化课成绩总分的80﹪。 <br />\r\n注：报名时请携带2张2寸彩色照片，现场填写报名申请表，领取准考证。 <br />\r\n</span></div>', '', 111, 0),
(14, '北京航空航天大学', 'upload/1313638011.jpg', NULL, '很不错的学校', '<span class="line160">北京航空航天大学是国家教育部正式批准现代远程教育的试点学校，在此基础上成立的北航现代远程教育学院，学院将以现成教育技术载体开展各层次的高等学历教育和大学后的非学历继续教育工作，充分发挥我校的科研教学优势，构建开放式办学网络。 </span>\r\n<p class="line160">　　北航现代远程教育系统充分利用了当前先进 的信息技术、网络视频技术，通过卫星数字通讯和计算机互联网实施远程教学传输，从而实现一点对多点的实时集中或非实时分散式教学。师生之间可以运用 ISDN视频会议系统的实时交互手段，也可以通过计算机互联网登陆中心网站的BBS、Chat、外交互服务器以及传送E-mail等功能作为非实时交互手 段来完成课堂讨论、答疑、作业布置、提交以及批阅。学员可根据当地站点的情况采取到站点集中收视学习，并随时通过互联网登陆或拨号上网的方式进入北航现代 远程教育网进行学习，真正感受现代教育所特有的魅力。实现不受时间、地点限制的学习。学生均由当地的教学站统一管理：即报名注册、集中教学、答疑讨论、考 试等统一在站点进行。现代远程学历教育采用完全学分制，柔性学习，专科升本科3年，高中升本科4年，可以上下各浮动1年。 <br />\r\n<br />\r\n北航现代远程教育学院将面向全国自主招生、采用完全学分制的柔性教学体系，以适应不同人员对高等教育的需要，对达到毕业、学位授予条例要求的毕业生颁 发国家承认学历的北京航空航天大学毕业证书及学位证书</p>', '很不错的学校', '<p>很不错的学校</p>', '<p>很不错的学校</p>', '<p>很不错的学校</p>', '<p>很不错的学校</p>', 0, 0),
(19, '江南大学', 'upload/1314325941.jpg', NULL, '2', '<p>江南大学享有&ldquo;轻工高等教育明珠&rdquo;的美誉，源于1902年创建的三江师范学堂；1958年该系整建制东迁无锡，建立无锡轻工业学院；1995年更名为无锡轻工大学；2001年经教育部批准，无锡轻工大学、江南学院、无锡教育学院合并组建江南大学。学校学科涉及理学、工学、农学、医学、文学、法学、经济学、管理学、教育学等9大门类，设有18个学院，共有63个本科专业，全日制在校本科学生逾二万人。江南大学已建设成为一所规模结构较为合理，教学质量优秀，办学效益显著，科研、教学、服务均得到社会公认，在国内外具有一定知名度的综合性大学</p>\r\n<p>江南大学坐落于太湖之滨的江南名城--江苏省无锡市，是教育部直属的国家&ldquo;211工程&rdquo;重点建设高校。目前，在教育部和省市政府的支持下，已基本建成占地3125亩、建筑面积95万平方米、以&ldquo;生态校园，曲水流觞&rdquo;为设计理念、设施先进、功能齐全、环境优美的蠡湖新校区。</p>', '3', '<p>4</p>', '<p>6</p>', '<p>5</p>', '<p>7</p>', 0, 0);
