INSERT INTO `wdonline` (`wd_id`, `s_name`, `wd_nc`, `wd_ask`, `wd_answer`, `wd_stime`, `wd_ltime`, `wd_bool`, `kid`, `wd_email`) VALUES
(53, '西南科技大学网院', '吴SIR', '我是吴宝新，我想读西南科技大学网络学院。请问需要什么报名条件。', '当然可以，只要肯出钱，想读啥学院都成。有钱能使鬼推磨.我认为有钱能使磨推鬼.', '2010-09-27 17:21:48', '2011-08-18 23:05:06', 1, NULL, NULL),
(55, '北京涉外艺术学院', '&#163;逝¤-', '我想学画画，你们学院有相关专业吗', '有，国画，山水画。油漆画，素描，啥画院都有。', '2010-09-28 09:17:53', '2011-05-24 09:57:27', 0, NULL, NULL),
(59, '安徽理工大学网院', '游客', '如保报名，去哪报名，带多少钱？', '带一张银行卡，要多少刷多少。方便。', '2010-10-21 21:28:56', '2010-10-21 21:33:13', 1, NULL, NULL),
(58, '安徽理工大学网院', '张明', '如何去查考试分数？', '就是这样查的呀', '2010-10-21 21:25:20', '2010-10-21 21:26:26', 1, NULL, NULL),
(84, '重庆师范网院', 'sdf', '你们学校有保障吗，学不会怎么办？', '有保障，学不会免费再学。', '2010-11-18 09:46:15', '2011-08-28 20:43:34', 1, NULL, NULL),
(109, '江南大学', '游客', '我想在你们那学一个专业可是我也不清楚自己想学什么专业！', '这个你可以具体的看下我们的课程设置极其所设的专业 然后来看看自己的兴趣爱好！', '2011-08-28 19:50:53', '2011-08-28 20:35:34', 1, NULL, NULL),
(82, '上海交通大学网院', '游客', '交大有工商管理专业吗，我想读硕士，请问需要办理什么手续？', '你好，有，只要带上身分证和毕业证书即可办理。', '2010-11-03 14:19:34', '2010-11-03 14:28:10', 1, NULL, NULL),
(83, '安徽理工大学网院', 'wqh_123', '报名需要什么样的照片规格是多少，或者你们提供现在拍照吗', '规格是一寸，我们现在也可以帮您拍照片的。', '2010-11-11 16:08:21', '2010-11-11 16:17:29', 1, NULL, NULL),
(103, '北京航空航天大学', '游客', '请问你们学校招收60多岁的人上课吗？', '汗、、这个我不太清楚', '2011-08-17 22:38:24', '2011-08-17 22:40:27', 1, NULL, NULL),
(86, '上海财务管理进修学院', '我要学财务', '会计班包拿会计证吗', '包拿会计证的。', '2010-12-07 15:38:22', '2010-12-09 10:11:59', 1, 23, NULL),
(104, '北京航空航天大学', '游客', '你们有哪些专业?', '这个你可以到我们的网站上具体的了解下', '2011-08-17 22:42:26', '2011-08-17 22:44:35', 1, NULL, NULL),
(90, '上海财务管理进修学院', '罗检', '请问湖南有什么大学可以选择？', '有，湖南湘潭大学。可以参考一下。', '2010-12-09 14:42:59', '2011-05-24 09:57:51', 1, 23, ''),
(91, '上海财务管理进修学院', '英语小子', '请问你们英语学校有哪些。', '有，全四六级，GRE，托福考试，不过可以重新再学。', '2010-12-09 15:38:02', '2011-05-24 09:57:41', 1, 25, ''),
(108, '上海财务管理进修学院', '顶替', '我测试问题之上海财务学校', '测试回复', '2011-08-23 00:22:46', '2011-08-23 00:24:48', 1, 23, 'sdfs@sdfo.com'),
(101, '上海交通大学网院', '游客', '我是王进，我初中文化，可以上交大网院吗', '可以，还过先读高中。', '2011-08-17 01:18:08', '2011-08-28 20:44:12', 1, NULL, NULL),
(102, '北京航空航天大学', '游客', '请问你们北航是国家办的学校吗？还是民营的？', '我们这是属于国营的谢谢！', '2011-08-17 22:34:54', '2011-08-17 22:35:29', 1, NULL, NULL),
(99, '西南科技大学网院', '李平', '我要学自动化，能不能直接拿证？', NULL, '2011-08-10 14:18:15', '2011-08-10 14:18:15', 0, NULL, NULL),
(105, '西南科技大学网院', '游客', '我现在50多岁了请问我还能去你们那学专业吗?', '抱歉这个我具体的也回答不了的 得请示我们学校的领导哦 ！', '2011-08-18 22:59:26', '2011-08-18 23:00:58', 1, NULL, NULL),
(106, '上海交通大学网院', '游客', '我可以去你们学校游玩吗?', '这个是可以的', '2011-08-18 23:02:00', '2011-08-18 23:03:40', 1, NULL, NULL),
(110, '江南大学', '小李子', '我想咨询下如果我在学校内学校 到时候 考不了证或者不发证怎么弄？', '这个您可以放心的我们这绝对不会说比给你发证的 这点是有保障的！至于考不考的了证就得看您自己了如果您认真完成我们的课程绝对可以过的！', '2011-08-28 20:37:05', '2011-08-28 20:39:32', 1, NULL, NULL),
(111, '西南科技大学网院', '胡强', '411528199108186516', NULL, '2011-08-31 00:21:28', '2011-08-31 00:21:28', 0, NULL, NULL),
(112, '北京航空航天大学', '游客', 'iuloi', NULL, '2011-10-21 14:57:05', '2011-10-21 14:57:05', 0, NULL, NULL),
