INSERT INTO `xl_step` (`xl_id`, `xl_title`, `xl_keyword`, `xl_contant`, `xl_banner`, `xl_bmtj`, `xl_bmtel`, `xl_onegg`, `xl_gglink`) VALUES
(2, '一休教育网【网络招生】：专业高校招生报名网站：自学考试|网络学院|中外合作|国际中小学|计划外招生|高校招生简章', '学历招生-学历报名-自学考试-网络学院-中外合办-出国留学-国际中小学-初中升高中-上海自考-复旦自考-上外自考-同济大学自考-出国留学--网上报名-学历报名', '为高考学员及落榜学员提供最新最全的高校招生资讯、高校招生简章、专业介绍以及高校就读咨询和报名服务。上海高校招生，高校计划外招生，高校招生简章，自学考试，网络学院中外合办、出国留学。一休教育网：做国内专业的网络教育服务平台，电话：021-64684667', 'upload/1292921945.swf', '<p><span>1.</span>高中、职业高中、中专、技校、职校毕业生或同等学历者，本辅导班报名时不安排验证。<br />\r\n<span>2.</span>报名时须交一寸免冠近照2张，报考专升本须提交大专毕业证书复印件<br />\r\n<span>3.</span>即日起至2010年9月（每班限报50名，报满为止），每天9：00-19：00<br />\r\n<span>3.</span>即日起至2010年9月（每班限报50名，报满为止），每天9：00-19：00<br />\r\n<span><strong>特别告知：</strong>出勤率达到90%切报考志愿仅填写同济大学而未被录取者全额退还学费（签订培训学历）</span></p>', '021-6844667', 'upload/1289444961.jpg', 'http://www.lixin.edu.cn/fo/pages/outer/index.jsp');
