INSERT INTO `yx_kaike` (`yxk_id`, `yxk_cl`, `class_title`, `yxk_school`, `yxk_title`, `pxk_pic`, `yxk_con`, `yxk_xfei`, `yxk_yhui`, `yxk_time`, `yxk_xshi`, `yxk_tel`, `yxk_qq`, `yxk_duix`, `yxk_adder`, `yxk_line`, `pxk_bool`) VALUES
(39, 'MBA/EMBA', 14, 8, '高级工商管理研修班招生简章', '', '<p>*全面系统地掌握企业高层管理者需要了解的人力资源、市场营销、财务管理、战略发展等专业知识；<br />\r\n*掌握先进而实用的管理方法，形成自己的管理理论框架，从战略性的角度思考现代企业及高层管理者所面对的问题，提升分析和解决实际管理问题的能力，帮助公司领先于竞争对手并不断自我创新；<br />\r\n*在复旦校友中建立宝贵的联络网，以便在职业生涯发展中共同探讨中国与全球市场所面临的机遇与挑战。<br />\r\n招生对象<br />\r\n1）国有大中型企、事业单位的中、高层管理人员<br />\r\n2）外资企业、民营企业的董事长、总经理、副总、高级经理<br />\r\n报名条件：<br />\r\n1）遵纪守法，品行端正，部门经理以上职位，5年以上工作经验；<br />\r\n2）受到知名企业推荐者优先考虑；<br />\r\n3）能够每月安排两天周末学习者。<br />\r\n学位与证书<br />\r\n修完全部课程合格者，将获得复旦大学颁发的《高级工商管理研修班》结业证明。</p>', '38000', '38000', '2011-11-19', '一年', '- - - - - -', '', '', '复旦大学', '', '1'),
(40, '在职研究生', 15, 8, '2011年金融学专业在职研究生班招生简章', '', '<div align="center"><strong>金融学专业</strong></div>\r\n<p>\r\n<table width="597" cellspacing="0" cellpadding="0" border="1">\r\n    <tbody>\r\n        <tr>\r\n            <td width="57" valign="top">\r\n            <div align="center"><strong>序号</strong></div>\r\n            </td>\r\n            <td width="180" valign="top">\r\n            <div align="center"><strong>课程</strong></div>\r\n            </td>\r\n            <td width="66" valign="top">\r\n            <div align="center"><strong>学分</strong></div>\r\n            </td>\r\n            <td width="66" valign="top">\r\n            <div align="center"><strong>序号</strong></div>\r\n            </td>\r\n            <td width="170" valign="top">\r\n            <div align="center"><strong>课程</strong></div>\r\n            </td>\r\n            <td width="58" valign="top">\r\n            <div align="center"><strong>学分</strong></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td width="57" valign="top">\r\n            <div align="center"><strong>1</strong></div>\r\n            </td>\r\n            <td width="180" valign="top">\r\n            <div align="center"><strong>政治经济学</strong></div>\r\n            </td>\r\n            <td width="66" valign="top">\r\n            <div align="center"><strong>3</strong></div>\r\n            </td>\r\n            <td width="66" valign="top">\r\n            <div align="center"><strong>7</strong></div>\r\n            </td>\r\n            <td width="170" valign="top">\r\n            <div align="center"><strong>经济法</strong></div>\r\n            </td>\r\n            <td width="58" valign="top">\r\n            <div align="center"><strong>3</strong></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td width="57" valign="top">\r\n            <div align="center"><strong>2</strong></div>\r\n            </td>\r\n            <td width="180" valign="top">\r\n            <div align="center"><strong>微观